INSERT INTO LithologyType VALUES('compoundMaterial','compound material','An Earth Material composed of an aggregation of particles of Earth Material, possibly including other Compound Materials.','http://inspire.ec.europa.eu/codelist/LithologyValue/compoundMaterial');
INSERT INTO LithologyType VALUES('anthropogenicMaterial','anthropogenic material','Material known to have artificial (human-related) origin; insufficient information to classify in more detail.','http://inspire.ec.europa.eu/codelist/LithologyValue/anthropogenicMaterial');
INSERT INTO LithologyType VALUES('anthropogenicConsolidatedMaterial','anthropogenic consolidated material','Consolidated material known to have artificial (human-related) origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/anthropogenicConsolidatedMaterial');
INSERT INTO LithologyType VALUES('anthropogenicUnconsolidatedMaterial','anthropogenic unconsolidated material','Unconsolidated material known to have artificial (human-related) origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/anthropogenicUnconsolidatedMaterial');
INSERT INTO LithologyType VALUES('breccia','breccia','Coarse-grained material composed of angular broken rock fragments; the fragments typically have sharp edges and unworn corners.','http://inspire.ec.europa.eu/codelist/LithologyValue/breccia');
INSERT INTO LithologyType VALUES('compositeGenesisMaterial','composite genesis material','Material of unspecified consolidation state formed by geological modification of pre-existing materials outside the realm of igneous and sedimentary processes.','http://inspire.ec.europa.eu/codelist/LithologyValue/compositeGenesisMaterial');
INSERT INTO LithologyType VALUES('compositeGenesisRock','composite genesis rock','Rock formed by geological modification of pre-existing rocks outside the realm of igneous and sedimentary processes.','http://inspire.ec.europa.eu/codelist/LithologyValue/compositeGenesisRock');
INSERT INTO LithologyType VALUES('faultRelatedMaterial','fault-related material','Material formed as a result of brittle faulting, composed of greater than 10 percent matrix; matrix is fine-grained material caused by tectonic grainsize reduction.','http://inspire.ec.europa.eu/codelist/LithologyValue/faultRelatedMaterial');
INSERT INTO LithologyType VALUES('impactGeneratedMaterial','impact generated material','Material that contains features indicative of shock metamorphism, such as microscopic planar deformation features within grains or shatter cones, interpreted to be the result of extraterrestrial bolide impact. Includes breccias and melt rocks.','http://inspire.ec.europa.eu/codelist/LithologyValue/impactGeneratedMaterial');
INSERT INTO LithologyType VALUES('materialFormedInSurficialEnvironment','material formed in surficial environment','Material that is the product of weathering processes operating on pre-existing rocks or deposits, analogous to hydrothermal or metasomatic rocks, but formed at ambient Earth surface temperature and pressure.','http://inspire.ec.europa.eu/codelist/LithologyValue/materialFormedInSurficialEnvironment');
INSERT INTO LithologyType VALUES('rock','rock','Consolidated aggregate of one or more EarthMaterials, or a body of undifferentiated mineral matter, or of solid organic material.','http://inspire.ec.europa.eu/codelist/LithologyValue/rock');
INSERT INTO LithologyType VALUES('aphanite','aphanite','Rock that is too fine grained to categorize in more detail.','http://inspire.ec.europa.eu/codelist/LithologyValue/aphanite');
INSERT INTO LithologyType VALUES('sedimentaryRock','sedimentary rock','Rock formed by accumulation and cementation of solid fragmental material deposited by air, water or ice, or as a result of other natural agents, such as precipitation from solution, the accumulation of organic material, or from biogenic processes, including secretion by organisms.','http://inspire.ec.europa.eu/codelist/LithologyValue/sedimentaryRock');
INSERT INTO LithologyType VALUES('tuffite','tuffite','Rock consists of more than 50 percent particles of indeterminate pyroclastic or epiclastic origin and less than 75 percent particles of clearly pyroclastic origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/tuffite');
INSERT INTO LithologyType VALUES('sedimentaryMaterial','sedimentary material','Material formed by accumulation of solid fragmental material deposited by air, water or ice, or material that accumulated by other natural agents such as chemical precipitation from solution or secretion by organisms.','http://inspire.ec.europa.eu/codelist/LithologyValue/sedimentaryMaterial');
INSERT INTO LithologyType VALUES('carbonateSedimentaryMaterial','carbonate sedimentary material','Sedimentary material in which at least 50 percent of the primary and/or recrystallized constituents are composed of one (or more) of the carbonate minerals calcite, aragonite and dolomite, in particles of intrabasinal origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateSedimentaryMaterial');
INSERT INTO LithologyType VALUES('chemicalSedimentaryMaterial','chemical sedimentary material','Sedimentary material that consists of at least 50 percent material produced by inorganic chemical processes within the basin of deposition. Includes inorganic siliceous, carbonate, evaporite, iron-rich, and phosphatic sediment classes.','http://inspire.ec.europa.eu/codelist/LithologyValue/chemicalSedimentaryMaterial');
INSERT INTO LithologyType VALUES('clasticSedimentaryMaterial','clastic sedimentary material','Sedimentary material of unspecified consolidation state in which at least 50 percent of the constituent particles were derived from erosion, weathering, or mass-wasting of pre-existing earth materials, and transported to the place of deposition by mechanical agents such as water, wind, ice and gravity.','http://inspire.ec.europa.eu/codelist/LithologyValue/clasticSedimentaryMaterial');
INSERT INTO LithologyType VALUES('nonClasticSiliceousSedimentaryMaterial','non-clastic siliceous sedimentary material','Sedimentary material that consists of at least 50 percent silicate mineral material, deposited directly by chemical or biological processes at the depositional surface, or in particles formed by chemical or biological processes within the basin of deposition.','http://inspire.ec.europa.eu/codelist/LithologyValue/nonClasticSiliceousSedimentaryMaterial');
INSERT INTO LithologyType VALUES('organicRichSedimentaryMaterial','organic rich sedimentary material','Sedimentary material in which 50 percent or more of the primary sedimentary material is organic carbon.','http://inspire.ec.europa.eu/codelist/LithologyValue/organicRichSedimentaryMaterial');
INSERT INTO LithologyType VALUES('igneousMaterial','igneous material','Earth material formed as a result of igneous processes, e.g. intrusion and cooling of magma in the crust, volcanic eruption.','http://inspire.ec.europa.eu/codelist/LithologyValue/igneousMaterial');
INSERT INTO LithologyType VALUES('fragmentalIgneousMaterial','fragmental igneous material','Igneous_material of unspecified consolidation state in which greater than 75 percent of the rock consists of fragments produced as a result of igneous rock-forming process.','http://inspire.ec.europa.eu/codelist/LithologyValue/fragmentalIgneousMaterial');
INSERT INTO LithologyType VALUES('acidicIgneousMaterial','acidic igneous material','Igneous material with more than 63 percent SiO2.','http://inspire.ec.europa.eu/codelist/LithologyValue/acidicIgneousMaterial');
INSERT INTO LithologyType VALUES('basicIgneousMaterial','basic igneous material','Igneous material with between 45 and 52 percent SiO2.','http://inspire.ec.europa.eu/codelist/LithologyValue/basicIgneousMaterial');
INSERT INTO LithologyType VALUES('igneousRock','igneous rock','Rock formed as a result of igneous processes, for example intrusion and cooling of magma in the crust, or volcanic eruption.','http://inspire.ec.europa.eu/codelist/LithologyValue/igneousRock');
INSERT INTO LithologyType VALUES('intermediateCompositionIgneousMaterial','intermediate composition igneous material','Igneous material with between 52 and 63 percent SiO2.','http://inspire.ec.europa.eu/codelist/LithologyValue/intermediateCompositionIgneousMaterial');
INSERT INTO LithologyType VALUES('unconsolidatedMaterial','unconsolidated material','CompoundMaterial composed of an aggregation of particles that do not adhere to each other strongly enough that the aggregate can be considered a solid in its own right.','http://inspire.ec.europa.eu/codelist/LithologyValue/unconsolidatedMaterial');
INSERT INTO LithologyType VALUES('naturalUnconsolidatedMaterial','natural unconsolidated material','Unconsolidated material known to have natural, i.e. not human-made, origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/naturalUnconsolidatedMaterial');
INSERT INTO LithologyType VALUES('sediment','sediment','Unconsolidated material consisting of an aggregation of particles transported or deposited by air, water or ice, or that accumulated by other natural agents, such as chemical precipitation, and that forms in layers on the Earth''s surface.','http://inspire.ec.europa.eu/codelist/LithologyValue/sediment');
INSERT INTO LithologyType VALUES('acidicIgneousRock','acidic igneous rock','Igneous rock with more than 63 percent SiO2.','http://inspire.ec.europa.eu/codelist/LithologyValue/acidicIgneousRock');
INSERT INTO LithologyType VALUES('alkaliFeldsparGranite','alkali feldspar granite','Granitic rock that has a plagioclase to total feldspar ratio less than 0.1. QAPF field 2.','http://inspire.ec.europa.eu/codelist/LithologyValue/alkaliFeldsparGranite');
INSERT INTO LithologyType VALUES('alkaliFeldsparRhyolite','alkali feldspar rhyolite','Rhyolitoid in which the ratio of plagioclase to total feldspar is less than 0.1. QAPF field 2.','http://inspire.ec.europa.eu/codelist/LithologyValue/alkaliFeldsparRhyolite');
INSERT INTO LithologyType VALUES('alkaliFeldsparSyenite','alkali feldspar syenite','Alkali feldspar syenitic rock that contains 0-5 percent quartz and no feldspathoid in the QAPF fraction. QAPF field 6.','http://inspire.ec.europa.eu/codelist/LithologyValue/alkaliFeldsparSyenite');
INSERT INTO LithologyType VALUES('alkaliFeldsparSyeniticRock','alkali feldspar syenitic rock','Syenitoid with a plagioclase to total feldspar ratio of less than 0.1. QAPF fields 6, 6*, and 6''.','http://inspire.ec.europa.eu/codelist/LithologyValue/alkaliFeldsparSyeniticRock');
INSERT INTO LithologyType VALUES('alkaliFeldsparTrachyte','alkali feldspar trachyte','Trachytoid that has a plagioclase to total feldspar ratio less than 0.1, between 0 and 5 percent quartz in the QAPF fraction, and no feldspathoid minerals. QAPF field 6.','http://inspire.ec.europa.eu/codelist/LithologyValue/alkaliFeldsparTrachyte');
INSERT INTO LithologyType VALUES('alkaliFeldsparTrachyticRock','alkali feldspar trachytic rock','Trachytoid that has a plagioclase to total feldspar ratio less than 0.1. QAPF fields 6, 6'', and 6*.','http://inspire.ec.europa.eu/codelist/LithologyValue/alkaliFeldsparTrachyticRock');
INSERT INTO LithologyType VALUES('alkaliOlivineBasalt','alkali olivine basalt','Alkali olivine basalt is silica-undersaturated, characterized by the absence of orthopyroxene, absence of quartz, presence of olivine, and typically contains some feldspathoid mineral, alkali feldspar or phlogopite in the groundmass. Feldspar phenocrysts typically are labradorite to andesine in composition. Augite is rich in titanium compared to augite in tholeiitic basalt. Alkali olivine basalt is relatively rich in sodium.','http://inspire.ec.europa.eu/codelist/LithologyValue/alkaliOlivineBasalt');
INSERT INTO LithologyType VALUES('amphibolite','amphibolite','Metamorphic rock mainly consisting of green, brown or black amphibole and plagioclase (including albite), which combined form 75 percent or more of the rock, and both of which are present as major constituents. The amphibole constitutes 50 percent or more of the total mafic constituents and is present in an amount of 30 percent or more; other common minerals include quartz, clinopyroxene, garnet, epidote-group minerals, biotite, titanite and scapolite.','http://inspire.ec.europa.eu/codelist/LithologyValue/amphibolite');
INSERT INTO LithologyType VALUES('andesite','andesite','Fine-grained igneous rock with less than 20 percent quartz and less than 10 percent feldspathoid minerals in the QAPF fraction, in which the ratio of plagioclase to total feldspar is greater 0.65. Includes rocks defined modally in QAPF fields 9 and 10 or chemically in TAS field O2 as andesite. Basalt and andesite, which share the same QAPF fields, are distinguished chemically based on silica content, with basalt defined to contain less than 52 weight percent silica. If chemical data are not available, the color index is used to distinguish the categories, with basalt defined to contain greater than 35 percent mafic minerals by volume or greater than 40 percent mafic minerals by weight. Typically consists of plagioclase (frequently zoned from labradorite to oligoclase), pyroxene, hornblende and/or biotite. Fine grained equivalent of dioritic rock.','http://inspire.ec.europa.eu/codelist/LithologyValue/andesite');
INSERT INTO LithologyType VALUES('anorthosite','anorthosite','Anorthositic rock that contains between 0 and 5 percent quartz and no feldspathoid mineral in the QAPF fraction. QAPF field 10.','http://inspire.ec.europa.eu/codelist/LithologyValue/anorthosite');
INSERT INTO LithologyType VALUES('anorthositicRock','anorthositic rock','Leucocratic phaneritic crystalline igneous rock consisting essentially of plagioclase, often with small amounts of pyroxene. By definition, colour index M is less than 10, and plagiclase to total feldspar ratio is greater than 0.9. Less than 20 percent quartz and less than 10 percent feldspathoid in the QAPF fraction. QAPF field 10, 10*, and 10''.','http://inspire.ec.europa.eu/codelist/LithologyValue/anorthositicRock');
INSERT INTO LithologyType VALUES('anthrazit','anthrazit','Coal that has vitrinite mean random reflectance greater than 2.0% (determined in conformance with ISO 7404-5). Less than 12-14 percent volatiles (dry, ash free), greater than 91 percent fixed carbon (dry, ash free basis). The highest rank coal; very hard, glossy, black, with semimetallic luster, semi conchoidal fracture.','http://inspire.ec.europa.eu/codelist/LithologyValue/anthrazit');
INSERT INTO LithologyType VALUES('aplite','aplite','Light coloured crystalline rock, characterized by a fine grained allotriomorphic-granular (aplitic, saccharoidal or xenomorphic) texture; typically granitic composition, consisting of quartz, alkali feldspar and sodic plagioclase.','http://inspire.ec.europa.eu/codelist/LithologyValue/aplite');
INSERT INTO LithologyType VALUES('arenit','arenit','Clastic sandstone that contains less than 10 percent matrix. Matrix is mud-size silicate minerals (clay, feldspar, quartz, rock fragments, and alteration products) of detrital or diagenetic nature.','http://inspire.ec.europa.eu/codelist/LithologyValue/arenit');
INSERT INTO LithologyType VALUES('ashAndLapilli','ash and lapilli','Tephra in which less than 25 percent of fragments are greater than 64 mm in longest dimension','http://inspire.ec.europa.eu/codelist/LithologyValue/ashAndLapilli');
INSERT INTO LithologyType VALUES('ashBrecciaBombOrBlockTephra','ash breccia, bomb, or block tephra','Tephra in which more than 25 percent of particles are greater than 64 mm in largest dimension. Includes ash breccia, bomb tephra and block tephra of Gillespie and Styles (1999)','http://inspire.ec.europa.eu/codelist/LithologyValue/ashBrecciaBombOrBlockTephra');
INSERT INTO LithologyType VALUES('ashTuffLapillistoneAndLapilliTuff','ash tuff, lapillistone, and lapilli tuff','Pyroclastic rock in which less than 25 percent of rock by volume are more than 64 mm in longest diameter. Includes tuff, lapilli tuff, and lapillistone.','http://inspire.ec.europa.eu/codelist/LithologyValue/ashTuffLapillistoneAndLapilliTuff');
INSERT INTO LithologyType VALUES('basalt','basalt','Fine-grained or porphyritic igneous rock with less than 20 percent quartz, and less than 10 percent feldspathoid minerals, in which the ratio of plagioclase to total feldspar is greater 0.65. Typically composed of calcic plagioclase and clinopyroxene; phenocrysts typically include one or more of calcic plagioclase, clinopyroxene, orthopyroxene, and olivine. Includes rocks defined modally in QAPF fields 9 and 10 or chemically in TAS field B as basalt. Basalt and andesite are distinguished chemically based on silica content, with basalt defined to contain less than 52 weight percent silica. If chemical data are not available, the color index is used to distinguish the categories, with basalt defined to contain greater than 35 percent mafic minerals by volume or greater than 40 percent mafic minerals by weight.','http://inspire.ec.europa.eu/codelist/LithologyValue/basalt');
INSERT INTO LithologyType VALUES('basanite','basanite','Tephritoid that has a plagioclase to total feldspar ratio greater than 0.9, and contains more than 10 percent normative (CIPW) olivine.','http://inspire.ec.europa.eu/codelist/LithologyValue/basanite');
INSERT INTO LithologyType VALUES('basaniticFoidite','basanitic foidite','Foiditoid that contains less than 90 percent feldspathoid minerals in the QAPF fraction, and has a plagioclase to total feldspar ratio that is greater than 0.5, with greater than 10 percent normative olivine.','http://inspire.ec.europa.eu/codelist/LithologyValue/basaniticFoidite');
INSERT INTO LithologyType VALUES('basicIgneousRock','basic igneous rock','Igneous rock with between 45 and 52 percent SiO2.','http://inspire.ec.europa.eu/codelist/LithologyValue/basicIgneousRock');
INSERT INTO LithologyType VALUES('bauxite','bauxite','Highly aluminous material containing abundant aluminium hydroxides (gibbsite, less commonly boehmite, diaspore) and aluminium-substituted iron oxides or hydroxides and generally minor or negligible kaolin minerals; may contain up to 20 percent quartz. Commonly has a pisolitic or nodular texture, and may be cemented.','http://inspire.ec.europa.eu/codelist/LithologyValue/bauxite');
INSERT INTO LithologyType VALUES('biogenicSediment','biogenic sediment','Sediment composed of greater than 50 percent material of biogenic origin. Because the biogenic material may be skeletal remains that are not organic, all biogenic sediment is not necessarily organic-rich.','http://inspire.ec.europa.eu/codelist/LithologyValue/biogenicSediment');
INSERT INTO LithologyType VALUES('biogenicSilicaSedimentaryRock','biogenic silica sedimentary rock','Sedimentary rock that consists of at least 50 percent silicate mineral material, deposited directly by biological processes at the depositional surface, or in particles formed by biological processes within the basin of deposition.','http://inspire.ec.europa.eu/codelist/LithologyValue/biogenicSilicaSedimentaryRock');
INSERT INTO LithologyType VALUES('bitumen','bitumen','A composite material composed primarily of bitumen and mineral aggregates, e.g. gravel','http://inspire.ec.europa.eu/codelist/LithologyValue/bitumen');
INSERT INTO LithologyType VALUES('bituminousCoal','bituminous coal','Coal that has vitrinite mean random reflectance greater than 0.6% and less than 2.0% (determined in conformance with ISO 7404-5), or has a gross calorific value greater than 24 MJ/kg (determined in conformance with ISO 1928). Hard, black, organic rich sedimentary rock; contains less than 91 percent fixed carbon on a dry, mineral-matter-free basis, and greater than 13-14 percent volatiles (dry, ash free). Formed from the compaction or induration of variously altered plant remains similar to those of peaty deposits.','http://inspire.ec.europa.eu/codelist/LithologyValue/bituminousCoal');
INSERT INTO LithologyType VALUES('boninite','boninite','andesitic rock that contains more than 8 percent MgO. Typically consists of phenocrysts of protoenstatite, orthopyroxene, clinopyroxene, and olivine in a glassy base full of crystallites, and exhibits textures characterisitc of rapid crystal growth.','http://inspire.ec.europa.eu/codelist/LithologyValue/boninite');
INSERT INTO LithologyType VALUES('boulderGravelSizeSediment','boulder gravel size sediment','Sediment containing greater than 30 percent boulder-size particles (greater than 256 mm in diameter)','http://inspire.ec.europa.eu/codelist/LithologyValue/boulderGravelSizeSediment');
INSERT INTO LithologyType VALUES('boundstone','boundstone','Sedimentary carbonate rock with preserved biogenic texture, whose original components were bound and encrusted together during deposition by the action of plants and animals during deposition, and remained substantially in the position of growth.','http://inspire.ec.europa.eu/codelist/LithologyValue/boundstone');
INSERT INTO LithologyType VALUES('brecciaGougeSeries','breccia-gouge series','Fault material with features such as void spaces (filled or unfilled), or unconsolidated matrix material between fragments, indicating loss of cohesion during deformation. Includes fault-related breccia and gouge.','http://inspire.ec.europa.eu/codelist/LithologyValue/brecciaGougeSeries');
INSERT INTO LithologyType VALUES('buildingRubble','building rubble','Material of various size originating from broken down buildings or former road coatings','http://inspire.ec.europa.eu/codelist/LithologyValue/buildingRubble');
INSERT INTO LithologyType VALUES('calcareousCarbonateSediment','calcareous carbonate sediment','Carbonate sediment with a calcite (plus aragonite) to dolomite ratio greater than 1 to 1. Includes lime-sediments.','http://inspire.ec.europa.eu/codelist/LithologyValue/calcareousCarbonateSediment');
INSERT INTO LithologyType VALUES('calcareousCarbonateSedimentaryMaterial','calcareous carbonate sedimentary material','Carbonate sedimentary material of unspecified consolidation state with a calcite (plus aragonite) to dolomite ratio greater than 1 to 1. Includes lime-sediments, limestone and dolomitic limestone.','http://inspire.ec.europa.eu/codelist/LithologyValue/calcareousCarbonateSedimentaryMaterial');
INSERT INTO LithologyType VALUES('calcareousCarbonateSedimentaryRock','calcareous carbonate sedimentary rock','Carbonate sedimentary rock with a calcite (plus aragonite) to dolomite ratio greater than 1 to 1. Includes limestone and dolomitic limestone.','http://inspire.ec.europa.eu/codelist/LithologyValue/calcareousCarbonateSedimentaryRock');
INSERT INTO LithologyType VALUES('carbonateMud','carbonate mud','Carbonate sediment composed of less than 25 percent clasts that have a maximum diameter more than 2 mm, and the ratio of sand size to mud size clasts is less than one.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateMud');
INSERT INTO LithologyType VALUES('carbonateMudstone','carbonate mudstone','Mudstone that consists of greater than 50 percent carbonate minerals of any origin in the mud size fraction.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateMudstone');
INSERT INTO LithologyType VALUES('carbonateOoze','carbonate ooze','ooze that consists of more than 50 percent carbonate skeletal remains','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateOoze');
INSERT INTO LithologyType VALUES('carbonateRichMud','carbonate rich mud','Mud size sediment that contains between 10 and 50 percent carbonate minerals in any size fraction. Carbonate origin is not specified.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateRichMud');
INSERT INTO LithologyType VALUES('carbonateRichMudstone','carbonate rich mudstone','Mudstone that contains between 10 and 50 percent carbonate minerals in the mud size fraction. Carbonate origin is not specified.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateRichMudstone');
INSERT INTO LithologyType VALUES('carbonateSediment','carbonate sediment','Sediment in which at least 50 percent of the primary and/or recrystallized constituents are composed of one (or more) of the carbonate minerals calcite, aragonite and dolomite, in particles of intrabasinal origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateSediment');
INSERT INTO LithologyType VALUES('carbonateSedimentaryRock','carbonate sedimentary rock','Sedimentary rock in which at least 50 percent of the primary and/or recrystallized constituents are composed of one (or more) of the carbonate minerals calcite, aragonite, magnesite or dolomite.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateSedimentaryRock');
INSERT INTO LithologyType VALUES('carbonateWackestone','carbonate wackestone','Carbonate sedimentary rock with discernible mud supported depositional texture and containing greater than 10 percent allochems, and constituent particles are of intrabasinal origin. If particles are not intrabasinal, categorization as a mudstone or wackestone should be considered.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonateWackestone');
INSERT INTO LithologyType VALUES('carbonatite','carbonatite','Igneous rock composed of more than 50 percent modal carbonate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/carbonatite');
INSERT INTO LithologyType VALUES('cataclasiteSeries','cataclasite series','Fault-related rock that maintained primary cohesion during deformation, with matrix comprising greater than 10 percent of rock mass; matrix is fine-grained material formed through grain size reduction by fracture as opposed to crystal plastic process that operate in mylonitic rock. Includes cataclasite, protocataclasite and ultracataclasite.','http://inspire.ec.europa.eu/codelist/LithologyValue/cataclasiteSeries');
INSERT INTO LithologyType VALUES('chalk','chalk','A generally soft, white, very fine-grained, extremely pure, porous limestone. It forms under marine conditions from the gradual accumulation of skeletal elements from minute planktonic green algae (cocoliths), associated with varying proportions of larger microscopic fragments of bivalves, foraminifera and ostracods. It is common to find flint and chert nodules embedded in chalk.','http://inspire.ec.europa.eu/codelist/LithologyValue/chalk');
INSERT INTO LithologyType VALUES('chloriteActinoliteEpidoteMetamorphicRock','chlorite actinolite epidote metamorphic rock','Metamorphic rock characterized by 50 percent or more of combined chlorite, actinolite and epidote. Category for rocks generally named greenschist or greenstone.','http://inspire.ec.europa.eu/codelist/LithologyValue/chloriteActinoliteEpidoteMetamorphicRock');
INSERT INTO LithologyType VALUES('clasticSediment','clastic sediment','Sediment in which at least 50 percent of the constituent particles were derived from erosion, weathering, or mass-wasting of pre-existing earth materials, and transported to the place of deposition by mechanical agents such as water, wind, ice and gravity.','http://inspire.ec.europa.eu/codelist/LithologyValue/clasticSediment');
INSERT INTO LithologyType VALUES('clasticSedimentaryRock','clastic sedimentary rock','Sedimentary rock in which at least 50 percent of the constituent particles were derived from erosion, weathering, or mass-wasting of pre-existing earth materials, and transported to the place of deposition by mechanical agents such as water, wind, ice and gravity.','http://inspire.ec.europa.eu/codelist/LithologyValue/clasticSedimentaryRock');
INSERT INTO LithologyType VALUES('clay','clay','Mud that consists of greater than 50 percent particles with grain size less than 0.004 mm','http://inspire.ec.europa.eu/codelist/LithologyValue/clay');
INSERT INTO LithologyType VALUES('claystone','claystone','Mudstone that contains no detectable silt, inferred to consist virtually entirely of clay-size particles.','http://inspire.ec.europa.eu/codelist/LithologyValue/claystone');
INSERT INTO LithologyType VALUES('cobbleGravelSizeSediment','cobble gravel size sediment','Sediment containing greater than 30 percent cobble-size particles (64-256 mm in diameter)','http://inspire.ec.europa.eu/codelist/LithologyValue/cobbleGravelSizeSediment');
INSERT INTO LithologyType VALUES('concrete','concrete','Hardening composite construction material composed primarily of coarse gravel, cement and water','http://inspire.ec.europa.eu/codelist/LithologyValue/concrete');
INSERT INTO LithologyType VALUES('conglomerate','conglomerate','Clastic sedimentary rock composed of at least 30 percent rounded to subangular fragments larger than 2 mm in diameter; typically contains finer grained material in interstices between larger fragments. If more than 15 percent of the fine grained matrix is of indeterminant clastic or diagenetic origin and the fabric is matrix supported, may also be categorized as wackestone. If rock has unsorted or poorly sorted texture with a wide range of particle sizes, may also be categorized as diamictite.','http://inspire.ec.europa.eu/codelist/LithologyValue/conglomerate');
INSERT INTO LithologyType VALUES('crystallineCarbonate','crystalline carbonate','Carbonate rock of indeterminate mineralogy in which diagenetic processes have obliterated any original depositional texture.','http://inspire.ec.europa.eu/codelist/LithologyValue/crystallineCarbonate');
INSERT INTO LithologyType VALUES('dacite','dacite','Fine grained or porphyritic crystalline rock that contains less than 90 percent mafic minerals, between 20 and 60 percent quartz in the QAPF fraction, and has a plagioclase to total feldspar ratio greater than 0.65. Includes rocks defined modally in QAPF fields 4 and 5 or chemically in TAS Field O3. Typcially composed of quartz and sodic plagioclase with minor amounts of biotite and/or hornblende and/or pyroxene; fine-grained equivalent of granodiorite and tonalite.','http://inspire.ec.europa.eu/codelist/LithologyValue/dacite');
INSERT INTO LithologyType VALUES('diamictite','diamictite','Unsorted or poorly sorted, clastic sedimentary rock with a wide range of particle sizes including a muddy matrix. Biogenic materials that have such texture are excluded. Distinguished from conglomerate, sandstone, mudstone based on polymodality and lack of structures related to transport and deposition of sediment by moving air or water. If more than 10 percent of the fine grained matrix is of indeterminant clastic or diagenetic origin and the fabric is matrix supported, may also be categorized as wacke.','http://inspire.ec.europa.eu/codelist/LithologyValue/diamictite');
INSERT INTO LithologyType VALUES('diamicton','diamicton','Unsorted or poorly sorted, clastic sediment with a wide range of particle sizes, including a muddy matrix. Biogenic materials that have such texture are excluded. Distinguished from conglomerate, sandstone, mudstone based on polymodality and lack of structures related to transport and deposition of sediment by moving air or water. Assignment to an other size class can be used in conjunction to indicate the dominant grain size.','http://inspire.ec.europa.eu/codelist/LithologyValue/diamicton');
INSERT INTO LithologyType VALUES('diorite','diorite','Phaneritic crystalline rock consisting of intermediate plagioclase, commonly with hornblende and often with biotite or augite; colour index M less than 90, sodic plagioclase (An0-An50), no feldspathoid, and between 0 and 5 percent quartz. Includes rocks defined modally in QAPF field 10 as diorite.','http://inspire.ec.europa.eu/codelist/LithologyValue/diorite');
INSERT INTO LithologyType VALUES('dioriticRock','dioritic rock','Phaneritic crystalline rock with M less than 90, consisting of intermediate plagioclase, commonly with hornblende and often with biotite or augite. A dioritoid with a plagioclase to total feldspar ratio (in the QAPF fraction) greater than 0.9. Includes rocks defined modally in QAPF fields 10, 10'' and 10*.','http://inspire.ec.europa.eu/codelist/LithologyValue/dioriticRock');
INSERT INTO LithologyType VALUES('dioritoid','dioritoid','Phaneritic crystalline igneous rock with M less than 90, consisting of intermediate plagioclase, commonly with hornblende and often with biotite or augite. Plagioclase to total feldspar ratio is greater that 0.65, and anorthite content of plagioclase is less than 50 percent. Less than 10 percent feldspathoid mineral and less than 20 percent quartz in the QAPF fraction. Includes rocks defined modally in QAPF fields 9 and 10 (and their subdivisions).','http://inspire.ec.europa.eu/codelist/LithologyValue/dioritoid');
INSERT INTO LithologyType VALUES('doleriticRock','doleritic rock','Dark colored gabbroic (basaltic) or dioritic (andesitic) rock intermediate in grain size between basalt and gabbro and composed of plagioclase, pyroxene and opaque minerals; often with ophitic texture. Typically occurs as hypabyssal intrusions. Includes dolerite, microdiorite, diabase and microgabbro.','http://inspire.ec.europa.eu/codelist/LithologyValue/doleriticRock');
INSERT INTO LithologyType VALUES('dolomite','dolomite','Pure carbonate sedimentary rock with a ratio of magnesium carbonate to calcite (plus aragonite) greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/dolomite');
INSERT INTO LithologyType VALUES('dolomiticOrMagnesianSedimentaryMaterial','dolomitic or magnesian sedimentary material','Carbonate sedimentary material of unspecified consolidation degree with a ratio of magnesium carbonate to calcite (plus aragonite) greater than 1 to 1. Includes dolomite sediment, dolostone, lime dolostone and magnesite-stone.','http://inspire.ec.europa.eu/codelist/LithologyValue/dolomiticOrMagnesianSedimentaryMaterial');
INSERT INTO LithologyType VALUES('dolomiticOrMagnesianSedimentaryRock','dolomitic or magnesian sedimentary rock','Carbonate sedimentary rock with a ratio of magnesium carbonate to calcite (plus aragonite) greater than 1 to 1. Includes dolostone, lime dolostone and magnesite-stone.','http://inspire.ec.europa.eu/codelist/LithologyValue/dolomiticOrMagnesianSedimentaryRock');
INSERT INTO LithologyType VALUES('dolomiticSediment','dolomitic sediment','Carbonate sediment with a ratio of magnesium carbonate to calcite (plus aragonite) greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/dolomiticSediment');
INSERT INTO LithologyType VALUES('duricrust','duricrust','Rock forming a hard crust or layer at or near the Earth''s surface at the time of formation, e.g. in the upper horizons of a soil, characterized by structures indicative of pedogenic origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/duricrust');
INSERT INTO LithologyType VALUES('eclogite','eclogite','Metamorphic rock composed of 75 percent or more (by volume) omphacite and garnet, both of which are present as major constituents, the amount of neither of them being higher than 75 percent (by volume) the presence of plagioclase precludes classification as an eclogite.','http://inspire.ec.europa.eu/codelist/LithologyValue/eclogite');
INSERT INTO LithologyType VALUES('evaporite','evaporite','Nonclastic sedimentary rock composed of at least 50 percent non-carbonate salts, including chloride, sulfate or borate minerals; formed through precipitation of mineral salts from a saline solution (non-carbonate salt rock).','http://inspire.ec.europa.eu/codelist/LithologyValue/evaporite');
INSERT INTO LithologyType VALUES('exoticAlkalineRock','exotic alkaline rock','Kimberlite, lamproite, or lamprophyre. Generally are potassic, mafic or ultramafic rocks. Olivine (commonly serpentinized in kimberlite), and phlogopite are significant constituents.','http://inspire.ec.europa.eu/codelist/LithologyValue/exoticAlkalineRock');
INSERT INTO LithologyType VALUES('exoticCompositionIgneousRock','exotic composition igneous rock','Rock with ''exotic'' mineralogical, textural or field setting characteristics; typically dark colored, with abundant phenocrysts. Criteria include: presence of greater than 10 percent melilite or leucite, or presence of kalsilite, or greater than 50 percent carbonate minerals. Includes Carbonatite, Melilitic rock, Kalsilitic rocks, Kimberlite, Lamproite, Leucitic rock and Lamprophyres.','http://inspire.ec.europa.eu/codelist/LithologyValue/exoticCompositionIgneousRock');
INSERT INTO LithologyType VALUES('exoticEvaporite','exotic evaporite','Evaporite that is not 50 percent halite or 50 percent gypsum or anhydrite.','http://inspire.ec.europa.eu/codelist/LithologyValue/exoticEvaporite');
INSERT INTO LithologyType VALUES('fineGrainedIgneousRock','fine grained igneous rock','Igneous rock in which the framework of the rock consists of crystals that are too small to determine mineralogy with the unaided eye; framework may include up to 50 percent glass. A significant percentage of the rock by volume may be phenocrysts. Includes rocks that are generally called volcanic rocks.','http://inspire.ec.europa.eu/codelist/LithologyValue/fineGrainedIgneousRock');
INSERT INTO LithologyType VALUES('foidBearingAlkaliFeldsparSyenite','foid bearing alkali feldspar syenite','Alkali feldspar syenitic rock that contains 0-10 percent feldspathoid mineral and no quartz in the QAPF fraction. QAPF field 6''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingAlkaliFeldsparSyenite');
INSERT INTO LithologyType VALUES('foidBearingAlkaliFeldsparTrachyte','foid bearing alkali feldspar trachyte','Alkali feldspar trachytic rock that contains no quartz and between 0 and 10 percent feldspathoid mineral in the QAPF fraction. QAPF field 6''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingAlkaliFeldsparTrachyte');
INSERT INTO LithologyType VALUES('foidBearingAnorthosite','foid bearing anorthosite','Anorthositic rock that contains between 0 and 10 percent feldspathoid mineral and no quartz in the QAPF fraction. QAPF field 10''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingAnorthosite');
INSERT INTO LithologyType VALUES('foidBearingDiorite','foid bearing diorite','Dioritic rock that contains between 0 and 10 percent feldspathoid minerals in the QAPF fraction. QAPF field 10''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingDiorite');
INSERT INTO LithologyType VALUES('foidBearingGabbro','foid bearing gabbro','Gabbroic rock that contains 0-10 percent feldspathoid minerals and no quartz in the QAPF fraction. QAPF field 10''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingGabbro');
INSERT INTO LithologyType VALUES('foidBearingLatite','foid bearing latite','Latitic rock that contains no quartz and between 0 and 10 percent feldspathoid minerals in the QAPF fraction. QAPF field 8''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingLatite');
INSERT INTO LithologyType VALUES('foidBearingMonzodiorite','foid bearing monzodiorite','Monzodioritic rock that contains between 0 and 10 percent feldspathoid mineral.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingMonzodiorite');
INSERT INTO LithologyType VALUES('foidBearingMonzogabbro','foid bearing monzogabbro','Monzogabbroic rock that contains 0 to 10 percent feldspathoid mineral in the QAPF fraction. QAPF field 9''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingMonzogabbro');
INSERT INTO LithologyType VALUES('foidBearingMonzonite','foid bearing monzonite','Monzonitic rock that contains 0-10 percent feldspathoid mineral and no quartz in the QAPF fraction. Includes rocks defined modally in QAPF Field 8''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingMonzonite');
INSERT INTO LithologyType VALUES('foidBearingSyenite','foid bearing syenite','Syenitic rock that contains between 0 and 10 percent feldspathoid mineral and no quartz in the QAPF fraction. Defined modally in QAPF Field 7''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingSyenite');
INSERT INTO LithologyType VALUES('foidBearingTrachyte','foid bearing trachyte','Trachytic rock that contains between 0 and 10 percent feldspathoid in the QAPF fraction, and no quartz. QAPF field 7''.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidBearingTrachyte');
INSERT INTO LithologyType VALUES('foidDiorite','foid diorite','Foid dioritoid in which the plagioclase to total feldspar ratio is greater than 0.9. Includes rocks defined modally in QAPF field 14.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidDiorite');
INSERT INTO LithologyType VALUES('foidDioritoid','foid dioritoid','Phaneritic crystalline igneous rock in which M is less than 90, the plagioclase to total feldspar ratio is greater than 0.5, feldspathoid minerals form 10-60 percent of the QAPF fraction, plagioclase has anorthite content less than 50 percent. These rocks typically contain large amounts of mafic minerals. Includes rocks defined modally in QAPF fields 13 and 14.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidDioritoid');
INSERT INTO LithologyType VALUES('foidGabbro','foid gabbro','Foid gabbroid that has a plagioclase to total feldspar ratio greater than 0.9. Includes rocks defined modally in QAPF field 14.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidGabbro');
INSERT INTO LithologyType VALUES('foidGabbroid','foid gabbroid','Phaneritic crystalline igneous rock in which M is less than 90, the plagioclase to total feldspar ratio is greater than 0.5, feldspathoids form 10-60 percent of the QAPF fraction, and plagioclase has anorthite content greater than 50 percent. These rocks typically contain large amounts of mafic minerals. Includes rocks defined modally in QAPF fields 13 and 14.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidGabbroid');
INSERT INTO LithologyType VALUES('foidMonzodiorite','foid monzodiorite','Foid dioritoid in which the plagioclase to total feldspar ratio is between 0.1 and 0.9. Includes rocks defined modally in QAPF field 13.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidMonzodiorite');
INSERT INTO LithologyType VALUES('foidMonzogabbro','foid monzogabbro','Foid gabbroid that has a plagioclase to total feldspar ratio between 0.5 and 0.9. Includes rocks defined modally in QAPF field 13.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidMonzogabbro');
INSERT INTO LithologyType VALUES('foidMonzosyenite','foid monzosyenite','Foid syenitoid rock that has a plagioclase to total feldspar ratio of between 0.1 and 0.5. Includes rocks defined modally in QAPF Field 12.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidMonzosyenite');
INSERT INTO LithologyType VALUES('foidSyenite','foid syenite','Foid syenitoid that has a plagioclase to total feldspar ratio of less than 0.1. Includes rocks defined modally in QAPF field 11.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidSyenite');
INSERT INTO LithologyType VALUES('foidSyenitoid','foid syenitoid','Phaneritic crystalline igneous rock with M less than 90, contains between 10 and 60 percent feldspathoid mineral in the QAPF fraction, and has a plagioclase to total feldspar ratio less than 0.5. Includes QAPF fields 11 and 12.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidSyenitoid');
INSERT INTO LithologyType VALUES('foidite','foidite','Foiditoid that contains greater than 90 percent feldspathoid minerals in the QAPF fraction.','http://inspire.ec.europa.eu/codelist/LithologyValue/foidite');
INSERT INTO LithologyType VALUES('foiditoid','foiditoid','Fine grained crystalline rock containing less than 90 percent mafic minerals and more than 60 percent feldspathoid minerals in the QAPF fraction. Includes rocks defined modally in QAPF field 15 or chemically in TAS field F.','http://inspire.ec.europa.eu/codelist/LithologyValue/foiditoid');
INSERT INTO LithologyType VALUES('foidolite','foidolite','Phaneritic crystalline rock containing more than 60 percent feldspathoid minerals in the QAPF fraction. Includes rocks defined modally in QAPF field 15','http://inspire.ec.europa.eu/codelist/LithologyValue/foidolite');
INSERT INTO LithologyType VALUES('foliatedMetamorphicRock','foliated metamorphic rock','Metamorphic rock in which 10 percent or more of the contained mineral grains are elements in a planar or linear fabric. Cataclastic or glassy character precludes classification with this concept.','http://inspire.ec.europa.eu/codelist/LithologyValue/foliatedMetamorphicRock');
INSERT INTO LithologyType VALUES('fragmentalIgneousRock','fragmental igneous rock','Igneous rock in which greater than 75 percent of the rock consists of fragments produced as a result of igneous rock-forming process. Includes pyroclastic rocks, autobreccia associated with lava flows and intrusive breccias. Excludes deposits reworked by epiclastic processes (see Tuffite)','http://inspire.ec.europa.eu/codelist/LithologyValue/fragmentalIgneousRock');
INSERT INTO LithologyType VALUES('framestone','framestone','Carbonate reef rock consisting of a rigid framework of colonies, shells or skeletons, with internal cavities filled with fine sediment; usually created through the activities of colonial organisms.','http://inspire.ec.europa.eu/codelist/LithologyValue/framestone');
INSERT INTO LithologyType VALUES('gabbro','gabbro','Gabbroic rock that contains between 0 and 5 percent quartz and no feldspathoid mineral in the QAPF fraction. Includes rocks defined modally in QAPF Field 10 as gabbro.','http://inspire.ec.europa.eu/codelist/LithologyValue/gabbro');
INSERT INTO LithologyType VALUES('gabbroicRock','gabbroic rock','Gabbroid that has a plagioclase to total feldspar ratio greater than 0.9 in the QAPF fraction. Includes QAPF fields 10*, 10, and 10''. This category includes the various categories defined in LeMaitre et al. (2002) based on the mafic mineralogy, but apparently not subdivided based on the quartz/feldspathoid content.','http://inspire.ec.europa.eu/codelist/LithologyValue/gabbroicRock');
INSERT INTO LithologyType VALUES('gabbroid','gabbroid','Phaneritic crystalline igneous rock that contains less than 90 percent mafic minerals, and up to 20 percent quartz or up to 10 percent feldspathoid in the QAPF fraction. The ratio of plagioclase to total feldspar is greater than 0.65, and anorthite content of the plagioclase is greater than 50 percent. Includes rocks defined modally in QAPF fields 9 and 10 and their subdivisions.','http://inspire.ec.europa.eu/codelist/LithologyValue/gabbroid');
INSERT INTO LithologyType VALUES('genericConglomerate','generic conglomerate','Sedimentary rock composed of at least 30 percent rounded to subangular fragments larger than 2 mm in diameter; typically contains finer grained material in interstices between larger fragments. If more than 15 percent of the fine grained matrix is of indeterminant clastic or diagenetic origin and the fabric is matrix supported, may also be categorized as wackestone. If rock has unsorted or poorly sorted texture with a wide range of particle sizes, may also be categorized as diamictite.','http://inspire.ec.europa.eu/codelist/LithologyValue/genericConglomerate');
INSERT INTO LithologyType VALUES('genericMudstone','generic mudstone','Sedimentary rock consisting of less than 30 percent gravel-size (2 mm) particles and with a mud to sand ratio greater than 1. Clasts may be of any composition or origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/genericMudstone');
INSERT INTO LithologyType VALUES('genericSandstone','generic sandstone','Sedimentary rock in which less than 30 percent of particles are greater than 2 mm in diameter (gravel) and the sand to mud ratio is at least 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/genericSandstone');
INSERT INTO LithologyType VALUES('glassRichIgneousRock','glass rich igneous rock','Igneous rock that contains greater than 50 percent massive glass.','http://inspire.ec.europa.eu/codelist/LithologyValue/glassRichIgneousRock');
INSERT INTO LithologyType VALUES('glassyIgneousRock','glassy igneous rock','Igneous rock that consists of greater than 80 percent massive glass.','http://inspire.ec.europa.eu/codelist/LithologyValue/glassyIgneousRock');
INSERT INTO LithologyType VALUES('glaukophanschiefer','glaukophanschiefer','A metamorphic rock of roughly basaltic composition, defined by the presence of glaucophane with lawsonite or epidote. Other minerals that may be present include jadeite, albite, chlorite, garnet, and muscovite (phengitic white mica). Typically fine-grained, dark colored. Category for rocks commonly referred to as blueschist.','http://inspire.ec.europa.eu/codelist/LithologyValue/glaukophanschiefer');
INSERT INTO LithologyType VALUES('gneiss','gneiss','Foliated metamorphic rock with bands or lenticles rich in granular minerals alternating with bands or lenticles rich in minerals with a flaky or elongate prismatic habit. Mylonitic foliation or well developed, continuous schistosity (greater than 50 percent of the rock consists of grains participate in a planar or linear fabric) precludes classification with this concept.','http://inspire.ec.europa.eu/codelist/LithologyValue/gneiss');
INSERT INTO LithologyType VALUES('grainstone','grainstone','Carbonate sedimentary rock with recognizable depositional fabric that is grain-supported, and constituent particles are of intrabasinal origin; contains little or no mud matrix. Distinction from sandstone is based on interpretation of intrabasinal origin of clasts and grain-supported fabric, but grainstone definition does not include a grain size criteria.','http://inspire.ec.europa.eu/codelist/LithologyValue/grainstone');
INSERT INTO LithologyType VALUES('granite','granite','Phaneritic crystalline rock consisting of quartz, alkali feldspar and plagioclase (typically sodic) in variable amounts, usually with biotite and/or hornblende. Includes rocks defined modally in QAPF Field 3.','http://inspire.ec.europa.eu/codelist/LithologyValue/granite');
INSERT INTO LithologyType VALUES('granitoid','granitoid','Phaneritic crystalline igneous rock consisting of quartz, alkali feldspar and/or plagioclase. Includes rocks defined modally in QAPF fields 2, 3, 4 and 5 as alkali feldspar granite, granite, granodiorite or tonalite.','http://inspire.ec.europa.eu/codelist/LithologyValue/granitoid');
INSERT INTO LithologyType VALUES('granodiorite','granodiorite','Phaneritic crystalline rock consisting essentially of quartz, sodic plagioclase and lesser amounts of alkali feldspar with minor hornblende and biotite. Includes rocks defined modally in QAPF field 4.','http://inspire.ec.europa.eu/codelist/LithologyValue/granodiorite');
INSERT INTO LithologyType VALUES('granofels','granofels','Metamorphic rock with granoblastic fabric and very little or no foliation (less than 10 percent of the mineral grains in the rock are elements in a planar or linear fabric). Grainsize not specified.','http://inspire.ec.europa.eu/codelist/LithologyValue/granofels');
INSERT INTO LithologyType VALUES('granulite','granulite','Metamorphic rock of high metamorphic grade in which Fe-Mg silicate minerals are dominantly hydroxl-free; feldspar must be present, and muscovite is absent; rock contains less than 90 percent mafic minerals, less than 75 percent calcite and/or dolomite, less than 75 percent quartz, less than 50 percent iron-bearing minerals (hematite, magnetite, limonite-group, siderite, iron-sulfides), and less than 50 percent calc-silicate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/granulite');
INSERT INTO LithologyType VALUES('gravel','gravel','Clastic sediment containing greater than 30 percent gravel-size particles (greater than 2.0 mm diameter). Gravel in which more than half of the particles are of epiclastic origin','http://inspire.ec.europa.eu/codelist/LithologyValue/gravel');
INSERT INTO LithologyType VALUES('gravelSizeSediment','gravel size sediment','Sediment containing greater than 30 percent gravel-size particles (greater than 2.0 mm diameter). Composition or gensis of clasts not specified.','http://inspire.ec.europa.eu/codelist/LithologyValue/gravelSizeSediment');
INSERT INTO LithologyType VALUES('gypsumOrAnhydrite','gypsum or anhydrite','Evaporite composed of at least 50 percent gypsum or anhydrite.','http://inspire.ec.europa.eu/codelist/LithologyValue/gypsumOrAnhydrite');
INSERT INTO LithologyType VALUES('highMagnesiumFineGrainedIgneousRock','high magnesium fine grained igneous rock','fine-grained igneous rock that contains unusually high concentration of MgO. For rocks that contain greater than 52 percent silica, MgO must be greater than 8 percent. For rocks containing less than 52 percent silica, MgO must be greater than 12 percent.','http://inspire.ec.europa.eu/codelist/LithologyValue/highMagnesiumFineGrainedIgneousRock');
INSERT INTO LithologyType VALUES('hornblendite','hornblendite','Ultramafic rock that consists of greater than 40 percent hornblende plus pyroxene and has a hornblende to pyroxene ratio greater than 1. Includes olivine hornblendite, olivine-pyroxene hornblendite, pyroxene hornblendite, and hornblendite.','http://inspire.ec.europa.eu/codelist/LithologyValue/hornblendite');
INSERT INTO LithologyType VALUES('hornfels','hornfels','Granofels formed by contact metamorphism, composed of a mosaic of equidimensional grains in a characteristically granoblastic or decussate matrix; porphyroblasts or relict phenocrysts may be present. Typically fine grained.','http://inspire.ec.europa.eu/codelist/LithologyValue/hornfels');
INSERT INTO LithologyType VALUES('hybridSediment','hybrid sediment','Sediment that does not fit any of the other sediment composition/genesis categories. Sediment consisting of three or more components which form more than 5 percent but less than 50 precent of the material.','http://inspire.ec.europa.eu/codelist/LithologyValue/hybridSediment');
INSERT INTO LithologyType VALUES('hybridSedimentaryRock','hybrid sedimentary rock','Sedimentary rock that does not fit any of the other composition/genesis categories. Sedimentary rock consisting of three or more components which form more than 5 percent but less than 50 precent of the material.','http://inspire.ec.europa.eu/codelist/LithologyValue/hybridSedimentaryRock');
INSERT INTO LithologyType VALUES('impureCalcareousCarbonateSediment','impure calcareous carbonate sediment','Carbonate sediment in which between 50 and 90 percent of the constituents are composed of one (or more) of the carbonate minerals in particles of intrabasinal origin, and a calcite (plus aragonite) to dolomite ratio greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/impureCalcareousCarbonateSediment');
INSERT INTO LithologyType VALUES('impureCarbonateSediment','impure carbonate sediment','Carbonate sediment in which between 50 and 90 percent of the constituents are composed of one (or more) of the carbonate minerals in particles of intrabasinal origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/impureCarbonateSediment');
INSERT INTO LithologyType VALUES('impureCarbonateSedimentaryRock','impure carbonate sedimentary rock','Sedimentary rock in which between 50 and 90 percent of the primary and/or recrystallized constituents are composed of carbonate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/impureCarbonateSedimentaryRock');
INSERT INTO LithologyType VALUES('impureDolomite','impure dolomite','Impure carbonate sedimentary rock with a ratio of magnesium carbonate to calcite (plus aragonite) greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/impureDolomite');
INSERT INTO LithologyType VALUES('impureDolomiticSediment','impure dolomitic sediment','Carbonate sediment in which between 50 and 90 percent of the constituents are composed of one (or more) of the carbonate minerals in particles of intrabasinal origin, and the ratio of magnesium carbonate to calcite (plus aragonite) greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/impureDolomiticSediment');
INSERT INTO LithologyType VALUES('impureLimestone','impure limestone','Impure carbonate sedimentary rock with a calcite (plus aragonite) to dolomite ratio greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/impureLimestone');
INSERT INTO LithologyType VALUES('intermediateCompositionIgneousRock','intermediate composition igneous rock','Igneous rock with between 52 and 63 percent SiO2.','http://inspire.ec.europa.eu/codelist/LithologyValue/intermediateCompositionIgneousRock');
INSERT INTO LithologyType VALUES('ironRichSediment','iron rich sediment','Sediment that consists of at least 50 percent iron-bearing minerals (hematite, magnetite, limonite-group, siderite, iron-sulfides), as determined by hand-lens or petrographic analysis. Corresponds to a rock typically containing 15 percent iron by weight.','http://inspire.ec.europa.eu/codelist/LithologyValue/ironRichSediment');
INSERT INTO LithologyType VALUES('ironRichSedimentaryMaterial','iron rich sedimentary material','Sedimentary material of unspecified consolidation state that consists of at least 50 percent iron-bearing minerals (hematite, magnetite, limonite-group, siderite, iron-sulfides), as determined by hand-lens or petrographic analysis. Corresponds to a rock typically containing 15 percent iron by weight.','http://inspire.ec.europa.eu/codelist/LithologyValue/ironRichSedimentaryMaterial');
INSERT INTO LithologyType VALUES('ironRichSedimentaryRock','iron rich sedimentary rock','Sedimentary rock that consists of at least 50 percent iron-bearing minerals (hematite, magnetite, limonite-group, siderite, iron-sulfides), as determined by hand-lens or petrographic analysis. Corresponds to a rock typically containing 15 percent iron by weight.','http://inspire.ec.europa.eu/codelist/LithologyValue/ironRichSedimentaryRock');
INSERT INTO LithologyType VALUES('kalsiliticAndMeliliticRocks','kalsilitic and melilitic rocks','Igneous rock containing greater than 10 percent melilite or kalsilite. Typically undersaturated, ultrapotassic (kalsilitic rocks) or calcium-rich (melilitic rocks) mafic or ultramafic rocks.','http://inspire.ec.europa.eu/codelist/LithologyValue/kalsiliticAndMeliliticRocks');
INSERT INTO LithologyType VALUES('kohle','kohle','A consolidated organic sedimentary material having less than 75% moisture. This category includes low, medium, and high rank coals according to International Classification of In-Seam Coal (United Nations, 1998), thus including lignite. Sapropelic coal is not distinguished in this category from humic coals. Formed from the compaction or induration of variously altered plant remains similar to those of peaty deposits.','http://inspire.ec.europa.eu/codelist/LithologyValue/kohle');
INSERT INTO LithologyType VALUES('komatiiticRock','komatiitic rock','Ultramafic, magnesium-rich volcanic rock, typically with spinifex texture of intergrown skeletal and bladed olivine and pyroxene crystals set in abundant glass. Includes komatiite and meimechite.','http://inspire.ec.europa.eu/codelist/LithologyValue/komatiiticRock');
INSERT INTO LithologyType VALUES('latite','latite','Latitic rock that contains between 0 and 5 percent quartz and no feldspathoid in the QAPF fraction. QAPF field 8.','http://inspire.ec.europa.eu/codelist/LithologyValue/latite');
INSERT INTO LithologyType VALUES('latiticRock','latitic rock','Trachytoid that has a plagioclase to total feldspar ratio between 0.35 and 0.65. QAPF fields 8, 8'' and 8*.','http://inspire.ec.europa.eu/codelist/LithologyValue/latiticRock');
INSERT INTO LithologyType VALUES('lignite','lignite','Coal that has a gross calorific value less than 24 MJ/kg (determined in conformance with ISO 1928), and vitrinite mean random reflectance less than 0.6% (determined in conformance with ISO 7404-5). Gross calorific value is recalculated to a moist, ash free basis using bed moisture (determined according to ISO 1015 or ISO 5068). Includes all low-rank coals, including sub-bitiminous coal. A consolidated, dull, soft brown to black coal having many readily discernible plant fragments set in a finer grained organic matrix. Tends to crack and fall apart on drying. Operationally sub-bituminous and bitiminous coal are qualitatively distinguished based on brown streak for sub-bitiminous coal and black streak for bituminous coal.','http://inspire.ec.europa.eu/codelist/LithologyValue/lignite');
INSERT INTO LithologyType VALUES('limestone','limestone','Pure carbonate sedimentary rock with a calcite (plus aragonite) to dolomite ratio greater than 1 to 1. Includes limestone and dolomitic limestone.','http://inspire.ec.europa.eu/codelist/LithologyValue/limestone');
INSERT INTO LithologyType VALUES('marble','marble','Metamorphic rock consisting of greater than 75 percent fine- to coarse-grained recrystallized calcite and/or dolomite; usually with a granoblastic, saccharoidal texture.','http://inspire.ec.europa.eu/codelist/LithologyValue/marble');
INSERT INTO LithologyType VALUES('metamorphicRock','metamorphic rock','Rock formed by solid-state mineralogical, chemical and/or structural changes to a pre-existing rock, in response to marked changes in temperature, pressure, shearing stress and chemical environment.','http://inspire.ec.europa.eu/codelist/LithologyValue/metamorphicRock');
INSERT INTO LithologyType VALUES('metasomaticRock','metasomatic rock','Rock that has fabric and composition indicating open-system mineralogical and chemical changes in response to interaction with a fluid phase, typically water rich.','http://inspire.ec.europa.eu/codelist/LithologyValue/metasomaticRock');
INSERT INTO LithologyType VALUES('micaSchist','mica schist','A schist that consists of more than 50 percent mica minerals, typically muscovite or biotite. Special type included to distinguish this common variety of schist.','http://inspire.ec.europa.eu/codelist/LithologyValue/micaSchist');
INSERT INTO LithologyType VALUES('migmatite','migmatite','Silicate metamorphic rock that is pervasively heterogeneous on a decimeter to meter scale that typically consists of darker and lighter parts; the darker parts usually exhibit features of metamorphic rocks whereas the lighter parts are of igneous-looking appearance.','http://inspire.ec.europa.eu/codelist/LithologyValue/migmatite');
INSERT INTO LithologyType VALUES('mineDumpMaterial','mine dump material','spoil, overburden and other material excavated in mining','http://inspire.ec.europa.eu/codelist/LithologyValue/mineDumpMaterial');
INSERT INTO LithologyType VALUES('monzodiorite','monzodiorite','Phaneritic crystalline igneous rock consisting of sodic plagioclase (An0 to An50), alkali feldspar, hornblende and biotite, with or without pyroxene, and 0 to 5 percent quartz. Includes rocks defined modally in QAPF field 9.','http://inspire.ec.europa.eu/codelist/LithologyValue/monzodiorite');
INSERT INTO LithologyType VALUES('monzodioriticRock','monzodioritic rock','Phaneritic crystalline igneous rock consisting of sodic plagioclase (An0 to An50), alkali feldspar, hornblende and biotite, with or without pyroxene, and 0 to 10 percent feldspathoid or 0 to 20 percent quartz in the QAPF fraction. Plagioclase to total feldspar ratio in the QAPF fraction is between 0.65 and 0.9. Includes rocks defined modally in QAPF field 9, 9'' and 9* as monzodiorite, foid-beaing monzodiorite, and quartz monzodiorite.','http://inspire.ec.europa.eu/codelist/LithologyValue/monzodioriticRock');
INSERT INTO LithologyType VALUES('monzogabbro','monzogabbro','Monzogabbroic rock that contains between 0 an 5 percent quartz and no feldspathoid mineral in the QAPF fraction. Includes rocks defined modally in QAPF field 9 .','http://inspire.ec.europa.eu/codelist/LithologyValue/monzogabbro');
INSERT INTO LithologyType VALUES('monzogabbroicRock','monzogabbroic rock','Gabbroid with a plagioclase to total feldspar ratio between 0.65 and 0.9. QAPF field 9, 9 prime and 9 asterisk','http://inspire.ec.europa.eu/codelist/LithologyValue/monzogabbroicRock');
INSERT INTO LithologyType VALUES('monzogranite','monzogranite','Granite that has a plagiolcase to total feldspar ratio between 0.35 and 0.65. QAPF field 3b.','http://inspire.ec.europa.eu/codelist/LithologyValue/monzogranite');
INSERT INTO LithologyType VALUES('monzonite','monzonite','Monzonitic rock that contains 0-5 percent quartz and no feldspathoid mineral in the QAPF fraction. Includes rocks defined modally in QAPF Field 8.','http://inspire.ec.europa.eu/codelist/LithologyValue/monzonite');
INSERT INTO LithologyType VALUES('monzoniticRock','monzonitic rock','Syenitoid with a plagioclase to total feldspar ratio between 0.35 and 0.65. Includes rocks in QAPF fields 8, 8*, and 8''.','http://inspire.ec.europa.eu/codelist/LithologyValue/monzoniticRock');
INSERT INTO LithologyType VALUES('mud','mud','Clastic sediment consisting of less than 30 percent gravel-size (2 mm) particles and with a mud-size to sand-size particle ratio greater than 1. More than half of the particles are of epiclastic origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/mud');
INSERT INTO LithologyType VALUES('mudSizeSediment','mud size sediment','Sediment consisting of less than 30 percent gravel-size (2 mm) particles and with a mud-size to sand-size particle ratio greater than 1. Clasts may be of any composition or origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/mudSizeSediment');
INSERT INTO LithologyType VALUES('mudstone','mudstone','Clastic sedimentary rock consisting of less than 30 percent gravel-size (2 mm) particles and with a mud to sand ratio greater than 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/mudstone');
INSERT INTO LithologyType VALUES('myloniticRock','mylonitic rock','Metamorphic rock characterised by a foliation resulting from tectonic grain size reduction, in which more than 10 percent of the rock volume has undergone grain size reduction. Includes protomylonite, mylonite, ultramylonite, and blastomylonite.','http://inspire.ec.europa.eu/codelist/LithologyValue/myloniticRock');
INSERT INTO LithologyType VALUES('nonClasticSiliceousSediment','non-clastic siliceous sediment','Sediment that consists of at least 50 percent silicate mineral material, deposited directly by chemical or biological processes at the depositional surface, or in particles formed by chemical or biological processes within the basin of deposition.','http://inspire.ec.europa.eu/codelist/LithologyValue/nonClasticSiliceousSediment');
INSERT INTO LithologyType VALUES('nonClasticSiliceousSedimentaryRock','non-clastic siliceous sedimentary rock','Sedimentary rock that consists of at least 50 percent silicate mineral material, deposited directly by chemical or biological processes at the depositional surface, or in particles formed by chemical or biological processes within the basin of deposition.','http://inspire.ec.europa.eu/codelist/LithologyValue/nonClasticSiliceousSedimentaryRock');
INSERT INTO LithologyType VALUES('ooze','ooze','Biogenic sediment consisting of less than 1 percent gravel-size (greater than or equal to 2 mm) particles, with a sand to mud ratio less than 1 to 9, and less than 50 percent carbonate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/ooze');
INSERT INTO LithologyType VALUES('organicBearingMudstone','organic bearing mudstone','Mudstone that contains a significant amount of organic carbon, typically kerogen. Commonly finely laminated, brown or black in color.','http://inspire.ec.europa.eu/codelist/LithologyValue/organicBearingMudstone');
INSERT INTO LithologyType VALUES('organicRichSediment','organic rich sediment','Sediment with color, composition, texture and apparent density indicating greater than 50 percent organic content by weight on a moisture-free basis.','http://inspire.ec.europa.eu/codelist/LithologyValue/organicRichSediment');
INSERT INTO LithologyType VALUES('organicRichSedimentaryRock','organic rich sedimentary rock','Sedimentary rock with color, composition, texture and apparent density indicating greater than 50 percent organic content by weight on a moisture-free basis.','http://inspire.ec.europa.eu/codelist/LithologyValue/organicRichSedimentaryRock');
INSERT INTO LithologyType VALUES('orthogneiss','orthogneiss','A gneiss with mineralogy and texture indicating derivation from a phaneritic igneous rock protolith. Typically consists of abundant feldspar, with quartz, and variable hornblende, biotite, and muscovite, with a relatively homogeneous character.','http://inspire.ec.europa.eu/codelist/LithologyValue/orthogneiss');
INSERT INTO LithologyType VALUES('packstone','packstone','Carbonate sedimentary rock with discernible grain supported depositional texture, containing greater than 10 percent grains, and constituent particles are of intrabasinal origin; intergranular spaces are filled by matrix.','http://inspire.ec.europa.eu/codelist/LithologyValue/packstone');
INSERT INTO LithologyType VALUES('paragneiss','paragneiss','A gneiss with mineralogy and texture indicating derivation from a sedimentary rock protolith. Typically consists of abundant quartz, mica, or calcsilicate minerals; aluminosilicate minerals or garnet commonly present. Composition of rock tends to be more variable on a decimetric scale that in orthogneiss.','http://inspire.ec.europa.eu/codelist/LithologyValue/paragneiss');
INSERT INTO LithologyType VALUES('peat','peat','Unconsolidated organic-rich sediment composed of at least 50 percent semi-carbonised plant remains; individual remains commonly seen with unaided eye; yellowish brown to brownish black; generally fibrous texture; can be plastic or friable. In its natural state it can be readily cut and has a very high moisture content, generally greater than 90 percent. Liptinite to Inertinite ratio is less than one (Economic Commission for Europe, Committee on Sustainable Energy- United Nations (ECE-UN), 1998, International Classification of in-Seam Coals: Energy 19, 41 pp.)','http://inspire.ec.europa.eu/codelist/LithologyValue/peat');
INSERT INTO LithologyType VALUES('pebbleGravelSizeSediment','pebble gravel size sediment','Sediment containing greater than 30 percent pebble-size particles (2.0 -64 mm in diameter)','http://inspire.ec.europa.eu/codelist/LithologyValue/pebbleGravelSizeSediment');
INSERT INTO LithologyType VALUES('pegmatite','pegmatite','Exceptionally coarse grained crystalline rock with interlocking crystals; most grains are 1cm or more diameter; composition is generally that of granite, but the term may refer to the coarse grained facies of any type of igneous rock;usually found as irregular dikes, lenses, or veins associated with plutons or batholiths.','http://inspire.ec.europa.eu/codelist/LithologyValue/pegmatite');
INSERT INTO LithologyType VALUES('peridotite','peridotite','Ultramafic rock consisting of more than 40 percent (by volume) olivine with pyroxene and/or amphibole and little or no feldspar. Commonly altered to serpentinite. Includes rocks defined modally in the ultramafic rock classification as dunite, harzburgite, lherzolite, wehrlite, olivinite, pyroxene peridotite, pyroxene hornblende peridotite or hornblende peridotite.','http://inspire.ec.europa.eu/codelist/LithologyValue/peridotite');
INSERT INTO LithologyType VALUES('phaneriticIgneousRock','phaneritic igneous rock','Igneous rock in which the framework of the rock consists of individual crystals that can be discerned with the unaided eye. Bounding grain size is on the order of 32 to 100 microns. Igneous rocks with ''exotic'' composition are excluded from this concept.','http://inspire.ec.europa.eu/codelist/LithologyValue/phaneriticIgneousRock');
INSERT INTO LithologyType VALUES('phonolite','phonolite','Phonolitoid in which the plagioclase to total feldspar ratio is less than 0.1. Rock consists of alkali feldspar, feldspathoid minerals, and mafic minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/phonolite');
INSERT INTO LithologyType VALUES('phonoliticBasanite','phonolitic basanite','Tephritoid that has a plagioclase to total feldspar ratio between 0.5 and 0.9, and contains more than 10 percent normative (CIPW) olivine.','http://inspire.ec.europa.eu/codelist/LithologyValue/phonoliticBasanite');
INSERT INTO LithologyType VALUES('phonoliticFoidite','phonolitic foidite','Foiditoid that contains less than 90 percent feldspathoid minerals in the QAPF fraction, and has a plagioclase to total feldspar ratio that is less than 0.5','http://inspire.ec.europa.eu/codelist/LithologyValue/phonoliticFoidite');
INSERT INTO LithologyType VALUES('phonoliticTephrite','phonolitic tephrite','Tephritoid that has a plagioclase to total feldspar ratio between 0.5 and 0.9, and contains less than 10 percent normative (CIPW) olivine.','http://inspire.ec.europa.eu/codelist/LithologyValue/phonoliticTephrite');
INSERT INTO LithologyType VALUES('phonolitoid','phonolitoid','Fine grained igneous rock than contains less than 90 percent mafic minerals, between 10 and 60 percent feldspathoid mineral in the QAPF fraction and has a plagioclase to total feldspar ratio less than 0.5. Includes rocks defined modally in QAPF fields 11 and 12, and TAS field Ph.','http://inspire.ec.europa.eu/codelist/LithologyValue/phonolitoid');
INSERT INTO LithologyType VALUES('phosphateRichSediment','phosphate rich sediment','Sediment in which at least 50 percent of the primary and/or recrystallized constituents are phosphate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/phosphateRichSediment');
INSERT INTO LithologyType VALUES('phosphateRichSedimentaryMaterial','phosphate rich sedimentary material','Sedimentary material in which at least 50 percent of the primary and/or recrystallized constituents are phosphate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/phosphateRichSedimentaryMaterial');
INSERT INTO LithologyType VALUES('phosphorite','phosphorite','Sedimentary rock in which at least 50 percent of the primary or recrystallized constituents are phosphate minerals. Most commonly occurs as a bedded primary or reworked secondary marine rock, composed of microcrystalline carbonate fluorapatite in the form of lamina, pellets, oolites and nodules, and skeletal, shell and bone fragments.','http://inspire.ec.europa.eu/codelist/LithologyValue/phosphorite');
INSERT INTO LithologyType VALUES('phyllite','phyllite','Rock with a well developed, continuous schistosity, an average grain size between 0.1 and 0.5 millimeters, and a silvery sheen on cleavage surfaces. Individual phyllosilicate grains are barely visible with the unaided eye.','http://inspire.ec.europa.eu/codelist/LithologyValue/phyllite');
INSERT INTO LithologyType VALUES('phyllonite','phyllonite','Mylonitic rock composed largely of fine-grained mica that imparts a sheen to foliation surfaces; may have flaser lamination, isoclinal folding, and deformed veins, which indicate significant shearing. Macroscopically resembles phyllite, but formed by mechanical degradation of initially coarser rock.','http://inspire.ec.europa.eu/codelist/LithologyValue/phyllonite');
INSERT INTO LithologyType VALUES('porphyry','porphyry','Igneous rock that contains conspicuous phenocrysts in a finer grained groundmass; groundmass itself may be phaneritic or fine-grained.','http://inspire.ec.europa.eu/codelist/LithologyValue/porphyry');
INSERT INTO LithologyType VALUES('pureCalcareousCarbonateSediment','pure calcareous carbonate sediment','Carbonate sediment in which greater than 90 percent of the constituents are composed of one (or more) of the carbonate minerals in particles of intrabasinal origin, and a calcite (plus aragonite) to dolomite ratio greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/pureCalcareousCarbonateSediment');
INSERT INTO LithologyType VALUES('pureCarbonateMudstone','pure carbonate mudstone','Mudstone that consists of greater than 90 percent carbonate minerals of intrabasinal orign in the mud fraction, and contains less than 10 percent allochems. The original depositional texture is preserved and fabric is matrix supported. Carbonate mudstone of Dunham (1962)','http://inspire.ec.europa.eu/codelist/LithologyValue/pureCarbonateMudstone');
INSERT INTO LithologyType VALUES('pureCarbonateSediment','pure carbonate sediment','Carbonate sediment in which greater than 90 percent of the constituents are composed of one (or more) of the carbonate minerals in particles of intrabasinal origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/pureCarbonateSediment');
INSERT INTO LithologyType VALUES('pureCarbonateSedimentaryRock','pure carbonate sedimentary rock','Sedimentary rock in which greater than 90 percent of the primary and/or recrystallized constituents are carbonate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/pureCarbonateSedimentaryRock');
INSERT INTO LithologyType VALUES('pureDolomiticSediment','pure dolomitic sediment','Carbonate sediment in which greater than 90 percent of the constituents are composed of one (or more) of the carbonate minerals in particles of intrabasinal origin, and a ratio of magnesium carbonate to calcite (plus aragonite) greater than 1 to 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/pureDolomiticSediment');
INSERT INTO LithologyType VALUES('pyroclasticMaterial','pyroclastic material','Fragmental igneous material that consists of more than 75 percent of particles formed by disruption as a direct result of volcanic action.','http://inspire.ec.europa.eu/codelist/LithologyValue/pyroclasticMaterial');
INSERT INTO LithologyType VALUES('pyroclasticRock','pyroclastic rock','Fragmental igneous rock that consists of greater than 75 percent fragments produced as a direct result of eruption or extrusion of magma from within the earth onto its surface. Includes autobreccia associated with lava flows and excludes deposits reworked by epiclastic processes.','http://inspire.ec.europa.eu/codelist/LithologyValue/pyroclasticRock');
INSERT INTO LithologyType VALUES('pyroxenite','pyroxenite','Ultramafic phaneritic igneous rock composed almost entirely of one or more pyroxenes and occasionally biotite, hornblende and olivine. Includes rocks defined modally in the ultramafic rock classification as olivine pyroxenite, olivine-hornblende pyroxenite, pyroxenite, orthopyroxenite, clinopyroxenite and websterite.','http://inspire.ec.europa.eu/codelist/LithologyValue/pyroxenite');
INSERT INTO LithologyType VALUES('quartzAlkaliFeldsparSyenite','quartz alkali feldspar syenite','Alkali feldspar syenitic rock that contains 5 to 20 percent quartz and no feldspathoid in the QAPF fraction. QAPF field 6*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzAlkaliFeldsparSyenite');
INSERT INTO LithologyType VALUES('quartzAlkaliFeldsparTrachyte','quartz alkali feldspar trachyte','Alkali feldspar trachytic rock that contains and between 5 and 20 percent quartz mineral in the QAPF fraction. QAPF field 6*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzAlkaliFeldsparTrachyte');
INSERT INTO LithologyType VALUES('quartzAnorthosite','quartz anorthosite','Anorthositic rock that contains between 5 and 20 percent quartz in the QAPF fraction. QAPF field 10*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzAnorthosite');
INSERT INTO LithologyType VALUES('quartzDiorite','quartz diorite','Dioritic rock that contains between 5 to 20 percent quartz in the QAPF fraction. QAPF field 10*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzDiorite');
INSERT INTO LithologyType VALUES('quartzGabbro','quartz gabbro','Gabbroic rock that contains between 5 and 20 percent quartz in the QAPF fraction. QAPF field 10*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzGabbro');
INSERT INTO LithologyType VALUES('quartzLatite','quartz latite','Latitic rock that contains between 5 and 20 percent quartz in the QAPF fraction. QAPF field 8*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzLatite');
INSERT INTO LithologyType VALUES('quartzMonzodiorite','quartz monzodiorite','Monzodioritic rock that contains between 5 and 20 percent quartz.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzMonzodiorite');
INSERT INTO LithologyType VALUES('quartzMonzogabbro','quartz monzogabbro','Monzogabbroic rock that contains between 5 and 20 percent quartz in the QAPF fraction. QAPF field 9*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzMonzogabbro');
INSERT INTO LithologyType VALUES('quartzMonzonite','quartz monzonite','Monzonitic rock that contains 5-20 percent quartz iin the QAPF fraction. Includes rocks defined modally in QAPF Field 8*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzMonzonite');
INSERT INTO LithologyType VALUES('quartzRichIgneousRock','quartz rich igneous rock','Phaneritic crystalline igneous rock that contains less than 90 percent mafic minerals and contains greater than 60 percent quartz in the QAPF fraction.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzRichIgneousRock');
INSERT INTO LithologyType VALUES('quartzSyenite','quartz syenite','Syenitic rock that contains between 5 and 20 percent quartz in the QAPF fraction. Defined modally in QAPF Field 7*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzSyenite');
INSERT INTO LithologyType VALUES('quartzTrachyte','quartz trachyte','Trachytic rock that contains between 5 and 20 percent quartz in the QAPF fraction. QAPF field 7*.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzTrachyte');
INSERT INTO LithologyType VALUES('quartzite','quartzite','Metamorphic rock consisting of greater than or equal to 75 percent quartz; typically granoblastic texture.','http://inspire.ec.europa.eu/codelist/LithologyValue/quartzite');
INSERT INTO LithologyType VALUES('residualMaterial','residual material','Material of composite origin resulting from weathering processes at the Earth’s surface, with genesis dominated by removal of chemical constituents by aqueous leaching. Miinor clastic, chemical, or organic input may also contribute. Consolidation state is not inherent in definition, but typically material is unconsolidated or weakly consolidated.','http://inspire.ec.europa.eu/codelist/LithologyValue/residualMaterial');
INSERT INTO LithologyType VALUES('rhyolite','rhyolite','rhyolitoid in which the ratio of plagioclase to total feldspar is between 0.1 and 0.65.','http://inspire.ec.europa.eu/codelist/LithologyValue/rhyolite');
INSERT INTO LithologyType VALUES('rhyolitoid','rhyolitoid','fine_grained_igneous_rock consisting of quartz and alkali feldspar, with minor plagioclase and biotite, in a microcrystalline, cryptocrystalline or glassy groundmass. Flow texture is common. Includes rocks defined modally in QAPF fields 2 and 3 or chemically in TAS Field R as rhyolite. QAPF normative definition is based on modal mineralogy thus: less than 90 percent mafic minerals, between 20 and 60 percent quartz in the QAPF fraction, and ratio of plagioclse to total feldspar is less than 0.65.','http://inspire.ec.europa.eu/codelist/LithologyValue/rhyolitoid');
INSERT INTO LithologyType VALUES('rockSalt','rock salt','Evaporite composed of at least 50 percent halite.','http://inspire.ec.europa.eu/codelist/LithologyValue/rockSalt');
INSERT INTO LithologyType VALUES('Y','Y','Clastic sediment in which less than 30 percent of particles are gravel (greater than 2 mm in diameter) and the sand to mud ratio is at least 1. More than half of the particles are of epiclastic origin.','http://inspire.ec.europa.eu/codelist/LithologyValue/SAND');
INSERT INTO LithologyType VALUES('sandSizeSediment','sand size sediment','Sediment in which less than 30 percent of particles are gravel (greater than 2 mm in diameter) and the sand to mud ratio is at least 1. Composition or genesis of clasts not specified.','http://inspire.ec.europa.eu/codelist/LithologyValue/sandSizeSediment');
INSERT INTO LithologyType VALUES('sandstone','sandstone','Clastic sedimentary rock in which less than 30 percent of particles are greater than 2 mm in diameter (gravel) and the sand to mud ratio is at least 1.','http://inspire.ec.europa.eu/codelist/LithologyValue/sandstone');
INSERT INTO LithologyType VALUES('sapropel','sapropel','Jelly like organic rich sediment composed of plant remains, usually algal. Liptinite to Inertinite ratio is greater than one (Economic Commission for Europe, Committee on Sustainable Energy- United Nations (ECE-UN), 1998, International Classification of in-Seam Coals: Energy 19, 41 pp.)','http://inspire.ec.europa.eu/codelist/LithologyValue/sapropel');
INSERT INTO LithologyType VALUES('schist','schist','Foliated phaneritic metamorphic rock with well developed, continuous schistosity, meaning that greater than 50 percent of the rock by volume is mineral grains with a thin tabular, lamellar, or acicular prismatic crystallographic habit that are oriented in a continuous planar or linear fabric.','http://inspire.ec.europa.eu/codelist/LithologyValue/schist');
INSERT INTO LithologyType VALUES('serpentinite','serpentinite','Rock consisting of more than 75 percent serpentine-group minerals, eg. antigorite, chrysotile or lizardite; accessory chlorite, talc and magnetite may be present; derived from hydration of ferromagnesian silicate minerals such as olivine and pyroxene.','http://inspire.ec.europa.eu/codelist/LithologyValue/serpentinite');
INSERT INTO LithologyType VALUES('sewageSludge','sewage sludge','more or less liquid (when disposed) by-products of wastewater treatment','http://inspire.ec.europa.eu/codelist/LithologyValue/sewageSludge');
INSERT INTO LithologyType VALUES('shale','shale','Laminated mudstone that will part or break along thin, closely spaced layers parallel to stratification.','http://inspire.ec.europa.eu/codelist/LithologyValue/shale');
INSERT INTO LithologyType VALUES('silicateMud','silicate mud','Mud size sediment that consists of less than 50 percent carbonate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/silicateMud');
INSERT INTO LithologyType VALUES('silicateMudstone','silicate mudstone','Mudstone that contains less than 10 percent carbonate minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/silicateMudstone');
INSERT INTO LithologyType VALUES('siliceousOoze','siliceous ooze','ooze that consists of more than 50 percent siliceous skeletal remains','http://inspire.ec.europa.eu/codelist/LithologyValue/siliceousOoze');
INSERT INTO LithologyType VALUES('silt','silt','Mud that consists of greater than 50 percent silt-size grains.','http://inspire.ec.europa.eu/codelist/LithologyValue/silt');
INSERT INTO LithologyType VALUES('siltstone','siltstone','Mudstone that contains detectable silt. (see comments)','http://inspire.ec.europa.eu/codelist/LithologyValue/siltstone');
INSERT INTO LithologyType VALUES('skarn','skarn','Metasomatic rock consisting mainly of Ca-, Mg-, Fe-, or Mn-silicate minerals, which are free from or poor in water. Typically formed at the contact between a silicate rock or magma and a carbonate rock.','http://inspire.ec.europa.eu/codelist/LithologyValue/skarn');
INSERT INTO LithologyType VALUES('slag','slag','by-product of smelting ore and processing metals','http://inspire.ec.europa.eu/codelist/LithologyValue/slag');
INSERT INTO LithologyType VALUES('slate','slate','Compact, fine grained rock with an average grain size less than 0.032 millimeter and a well developed schistosity (slaty cleavage), and hence can be split into slabs or thin plates.','http://inspire.ec.europa.eu/codelist/LithologyValue/slate');
INSERT INTO LithologyType VALUES('sludge','sludge','More or less liquid (when disposed) by-products of industrial processes','http://inspire.ec.europa.eu/codelist/LithologyValue/sludge');
INSERT INTO LithologyType VALUES('soilImprover','soil improver','Material added to soil to improve plant growth and health','http://inspire.ec.europa.eu/codelist/LithologyValue/soilImprover');
INSERT INTO LithologyType VALUES('spilite','spilite','Altered basic to intermediate composition fine-grained igneous rock in which the feldspar is partially or completely composed of of albite, typically accompanied by chlorite, calcite, quartz, epidote, prehnite, and low-tempaerature hydrous crystallization products. Preservation of eruptive volcanic features is typical.','http://inspire.ec.europa.eu/codelist/LithologyValue/spilite');
INSERT INTO LithologyType VALUES('syenite','syenite','Syenitic rock that contains between 0 and 5 percent quartz and no feldspathoid mineral in the QAPF fraction. Defined modally in QAPF Field 7.','http://inspire.ec.europa.eu/codelist/LithologyValue/syenite');
INSERT INTO LithologyType VALUES('syeniticRock','syenitic rock','Syenitoid with a plagioclase to total feldspar ratio between 0.1 and 0.35. Includes rocks in QAPF fields 7, 7*, and 7''.','http://inspire.ec.europa.eu/codelist/LithologyValue/syeniticRock');
INSERT INTO LithologyType VALUES('syenitoid','syenitoid','Phaneritic crystalline igneous rock with M less than 90, consisting mainly of alkali feldspar and plagioclase; minor quartz or nepheline may be present, along with pyroxene, amphibole or biotite. Ratio of plagioclase to total feldspar is less than 0.65, quartz forms less than 20 percent of QAPF fraction, and feldspathoid minerals form less than 10 percent of QAPF fraction. Includes rocks classified in QAPF fields 6, 7 and 8 and their subdivisions.','http://inspire.ec.europa.eu/codelist/LithologyValue/syenitoid');
INSERT INTO LithologyType VALUES('syenogranite','syenogranite','Granite that has a plagiolcase to total feldspar ratio between 0.10 and 0.35. QAPF field 3a.','http://inspire.ec.europa.eu/codelist/LithologyValue/syenogranite');
INSERT INTO LithologyType VALUES('tephra','tephra','Unconsolidated pyroclastic material in which greater than 75 percent of the fragments are deposited as a direct result of volcanic processes and the deposit has not been reworked by epiclastic processes. Includes ash, lapilli tephra, bomb tephra, block tephra and unconsolidated agglomerate.','http://inspire.ec.europa.eu/codelist/LithologyValue/tephra');
INSERT INTO LithologyType VALUES('tephrite','tephrite','Tephritoid that has a plagioclase to total feldspar ratio greater than 0.9, and contains less than 10 percent normative (CIPW) olivine.','http://inspire.ec.europa.eu/codelist/LithologyValue/tephrite');
INSERT INTO LithologyType VALUES('tephriticFoidite','tephritic foidite','Foiditoid that contains less than 90 percent feldspathoid minerals in the QAPF fraction, and has a plagioclase to total feldspar ratio that is greater than 0.5, with less than 10 percent normative olivine','http://inspire.ec.europa.eu/codelist/LithologyValue/tephriticFoidite');
INSERT INTO LithologyType VALUES('tephriticPhonolite','tephritic phonolite','Phonolitoid that has a plagioclase to total feldspar ratio between 0.1 and 0.5. Broadly corresponds to TAS tephriphonolite of TAS field U3.','http://inspire.ec.europa.eu/codelist/LithologyValue/tephriticPhonolite');
INSERT INTO LithologyType VALUES('tephritoid','tephritoid','Fine grained igneous rock than contains less than 90 percent mafic minerals, between 10 and 60 percent feldspathoid mineral in the QAPF fraction and has a plagioclase to total feldspar ratio greater than 0.5. Includes rocks classified in QAPF field 13 and 14 or chemically in TAS field U1 as basanite or tephrite.','http://inspire.ec.europa.eu/codelist/LithologyValue/tephritoid');
INSERT INTO LithologyType VALUES('tholeiiticBasalt','tholeiitic basalt','Tholeiitic basalt is defined here to contain 2 pyroxene phases and interstitial quartz or tridymite or cristobalite in the groundmass. Pyroxene (augite and orthopyroxene or pigeonite) and calcium-rich plagioclase are common phenocryst minerals. Olivine may also be a phenocryst, and when present, may have rims of pigeonite. Only in tholeiitic basalt is olivine in reaction relationship with melt. Interstitial siliceous residue may be present, and is often glassy. Tholeiitic basalt is relatively poor in sodium. This category includes most basalts of the ocean floor, most large oceanic islands, and continental flood basalts such as the Columbia River Plateau.','http://inspire.ec.europa.eu/codelist/LithologyValue/tholeiiticBasalt');
INSERT INTO LithologyType VALUES('tonalite','tonalite','Granitoid consisting of quartz and intermediate plagioclase, usually with biotite and amphibole. Includes rocks defined modally in QAPF field 5; ratio of plagioclase to total feldspar is greater than 0.9.','http://inspire.ec.europa.eu/codelist/LithologyValue/tonalite');
INSERT INTO LithologyType VALUES('topsoil','topsoil','The dark-colored upper portion af a soil, varying in depth according to soil type.','http://inspire.ec.europa.eu/codelist/LithologyValue/topsoil');
INSERT INTO LithologyType VALUES('trachyte','trachyte','Trachytoid that has a plagioclase to total feldspar ratio between 0.1 and 0.35, between 0 and 5 percent quartz in the QAPF fraction, and no feldspathoid minerals. QAPF field 7.','http://inspire.ec.europa.eu/codelist/LithologyValue/trachyte');
INSERT INTO LithologyType VALUES('trachyticRock','trachytic rock','Trachytoid that has a plagioclase to total feldspar ratio between 0.1 and 0.35. QAPF fields 7, 7'', and 7*.','http://inspire.ec.europa.eu/codelist/LithologyValue/trachyticRock');
INSERT INTO LithologyType VALUES('trachytoid','trachytoid','Fine grained igneous rock than contains less than 90 percent mafic minerals, less than 10 percent feldspathoid mineral and less than 20 percent quartz in the QAPF fraction and has a plagioclase to total feldspar ratio less than 0.65. Mafic minerals typically include amphibole or mica; typically porphyritic. Includes rocks defined modally in QAPF fields 6, 7 and 8 (with subdivisions) or chemically in TAS Field T as trachyte or latite.','http://inspire.ec.europa.eu/codelist/LithologyValue/trachytoid');
INSERT INTO LithologyType VALUES('travertine','travertine','Biotically or abiotically precipitated calcium carbonate, from spring-fed, heated, or ambient-temperature water. May be white and spongy, various shades of orange, tan or gray, and ranges to dense, banded or laminated rock. Macrophytes, bryophytes, algae, cyanobacteria and other organisms often colonize the surface of travertine and may be preserved, to produce the porous varieties.','http://inspire.ec.europa.eu/codelist/LithologyValue/travertine');
INSERT INTO LithologyType VALUES('tuffBrecciaAgglomerateOrPyroclasticBreccia','tuff-breccia, agglomerate, or pyroclastic breccia','Pyroclastic rock in which greater than 25 percent of particles are greater than 64 mm in largest dimension. Includes agglomerate, pyroclastic breccia of Gillespie and Styles (1999)','http://inspire.ec.europa.eu/codelist/LithologyValue/tuffBrecciaAgglomerateOrPyroclasticBreccia');
INSERT INTO LithologyType VALUES('ultrabasicIgneousRock','ultrabasic igneous rock','Igneous rock with less than 45 percent SiO2.','http://inspire.ec.europa.eu/codelist/LithologyValue/ultrabasicIgneousRock');
INSERT INTO LithologyType VALUES('ultramaficIgneousRock','ultramafic igneous rock','Igneous rock that consists of greater than 90 percent mafic minerals.','http://inspire.ec.europa.eu/codelist/LithologyValue/ultramaficIgneousRock');
INSERT INTO LithologyType VALUES('wacke','wacke','Clastic sandstone with more than 10 percent matrix of indeterminate detrital or diagenetic nature. Matrix is mud size silicate minerals (clay, feldspar, quartz, rock fragments, and alteration products).','http://inspire.ec.europa.eu/codelist/LithologyValue/wacke');
